INSERT INTO `co_company` VALUES ('10061', '5QGQa9o', '北京华云合创科技有限公司成都分公司', '成都市', '', '', '拖欠工资/无故辞退/延迟离职/老板忽悠', '<p>老板只会吹牛逼,说过的福利，从来对不兑现，连接口也不找一个。\n项目排期先把需要做的事和时间定好，不考虑实际情况，而且随时改需求，一出问题就开人。之前开人吃了亏，现在就降薪降职，逼人走。\n工资不按时发放，之前是10号发工资，后来推迟到15号，再后来特么直接就不说话了，每个月发工资的时间看老板心情。</p>', '0', '0', '', '', '', '1466227549', '1', '0');
