INSERT INTO `co_company` VALUES ('10122', '70GGP0L', '网众天弘科技有限公司', '成都市', '', '', '变态管理/单休/被罚款', '<p>办公桌上除了电脑配套东西及笔记本一律不让放； 离开座位椅子不端正罚款30，穿短裤、拖鞋、裙子罚款100；必须转发微信推广，否则罚款200 ；看见老板不喊直接辞退；公司单休，节假日需听从安排去派发传单，包括技术人员。\n百度上说好的都是水军。</p>', '0', '0', '', '', '', '1491976902', '1', '0');
INSERT INTO `co_company` VALUES ('10123', 'njBeajQ', '明驰科技有限公司', '成都市', '', '', '非法辞退/经营不善', '<p>直接辞退员工，经营也不善，看能撑到几时吧。</p>', '0', '0', '', '', '', '1470727280', '1', '0');
INSERT INTO `co_company` VALUES ('10124', 'mv0w6za', '航科智宇', '成都市', '', '', '变相扣工资，加班严重', '<p>卸磨杀驴，迟到1分钟扣10元，扣双倍，迟到半小时扣当天三倍工资，旷工一天扣3天工资，加班不算加班，单休。绩效无故乱扣，人事是个大水逼，招聘消息什么六险一金，其实只有社保。\n迟到1分钟扣20，迟到3次扣工资的3%，承诺的事百分之90假，然后，离职申请上原因人事逼着离职人员写：赶进度、不适应。不让写实情，人事给谈的工资里面包括了30%的绩效。然而每月绩效扣几大百。每晚加班到11点算早，一般的12点-1点，这样都不算加班，是赶进度！</p>', '0', '0', '', '', '', '1486709921', '1', '0');
INSERT INTO `co_company` VALUES ('10125', '3Do9eMk', '极感科技', '成都市', '', '', '强辞退员工，压榨', '<p>招了新员工，三个月，就把人辞退了，老板是川大老师，公司全部是研究生，一个月500,外面招来的，一般不转正，项目需求的话，就会转正，但是，我和另外一个师兄转正后，今天被辞退了，说是公司资金周转不开，但是上礼拜我们刚刚集体帮助公司通过了cmmi认证，昨天宣布认证通过，国庆过后研一新生就来了，项目也不紧张，我们三个外面招来的人就被辞了，一个写需求文档妹儿和前端，他们也不缺人，有研究生被捏在手里的，他们要拿毕业证，只是我们的人，不要上当了。</p>', '0', '0', '', '', '', '1471680307', '1', '0');
INSERT INTO `co_company` VALUES ('10126', 'EbovzWM', '云聚互动', '成都市', '', '', '拖欠工资，不交社保', '<p>周朝江老板！拖欠工资、拖欠社保不交、需求无厘头、6个月没发过工资，开除测试，开除产品经理，人事都走了，还好意思说人家自己走，明明就是被开除，让会计去做运营，关键是开了人还没给一分钱。\n详请链接:<a href=\"http://mycd.qq.com/t-24184408-1.htm\">http://mycd.qq.com/t-24184408-1.htm </a></p>', '0', '0', '', '', '', '1477708120', '1', '0');
