INSERT INTO `co_company` VALUES ('10222', 'NZKAEzn', '深圳市极驱科技有限公司', '深圳市', '', '', '试用期没社保', '<p>打着找nodejs的招聘招人做vr游戏开发，说有补贴，补贴要各种发票，房租补贴竟然要我跟房东要发票</p>\n\n<p>推荐别人进公司工作，进一个签了合同就给一千，我推了两，两个都签合同了，奖金拖了我三四个月，然后打了老板的三四个手机号，要回一千，当时那公司的财务竟然还跟我要两千块钱的发票，才能给 我那两千块钱的奖金，还有一千块钱当然一直到现在也没给</p>\n\n<p>我也没有在那个公司工作了，那个老板还经常有事就在下班时间边过来，死都要拖到9点左右才走，试用期没有社保</p>', '0', '0', '', '', '', '1479883011', '1', '0');
