INSERT INTO `co_company` VALUES ('10233', 'dGoKyPZ', '深圳九指天下', '深圳市', '', '', '强制加班，拍脑袋打鸡血', '<p>1,强制加班，没到9点不能下班。准点下班就是员工有问题。</p>\n\n<p>2,运营经理提倡学习创业精神，要拼搏，要激情。整天开会占用时间，会议无意义居多。纯扯淡。</p>\n\n<p>3,kpi考核一天一个样。</p>\n\n<p>4,管理层没能力，新需求纯属拍脑袋跟抄竞品。就自己的需求能做。</p>\n\n<p>5,产品经理含总监全部集体辞职</p>', '0', '0', '', '', '', '1495161123', '1', '0');
