INSERT INTO `co_company` VALUES ('10237', 'Kom8od8', '深圳市睿海智电子科技有限公司', '深圳市', '', '', '老板态度恶劣', '<p>老板喜欢招刚毕业的 比较好妥协 工资比较低</p>\n\n<p>老板什么都要管 发个文件都要等老板审核 然后还一直在说什么事都要劳烦他</p>\n\n<p>老板每天上班不是考虑公司发展什么的 而是关注员工上招聘网或者投简历什么的</p>\n\n<p>上班各种理由让你无偿加班 周六不加班还骂人 晚上恶意不呆到晚上8点下班就威胁要开除</p>\n\n<p>奇怪的偏执 就因为开发把电脑带回家 就开除报警抓那人 专门找了律师咨询 律师说不构成罪名还不依不饶</p>\n\n<p>情商负数！傻逼！没有信用！整天画大饼！</p>\n\n<p>一大堆的吐槽 懒得一一写出来了 公司已经换了好几批人了</p>', '0', '0', '', '', '', '1481451235', '1', '0');
INSERT INTO `co_company` VALUES ('10238', '1G45PBd', '深圳博沃思教育文化传播公司', '深圳市', '', '', '管理层乱，无故劝退不给补偿', '<p>领导经常给员工画大饼，提出会提工资，不过一个月一个月过去，不了了之。</p>\n\n<p>领导甩锅情况严重，安排不当，说员工应变能力不足。</p>\n\n<p>公司管理层乱，股东制领导争权状况严重。没有太一致的观念。</p>\n\n<p>用人唯亲，无故劝退不给补偿。</p>', '0', '0', '', '', '', '1504334333', '1', '0');
