INSERT INTO `co_company` VALUES ('10282', 'eG999JP', '深圳吉祥腾达科技有限公司', '深圳市', '', '', '加班、劝退不补偿、福利差', '<p>1、加班是家常便饭，几乎每天晚上都要加班到8点半之后。还不给补偿。</p>\n\n<p>2、工作氛围很压抑。每天上班感觉活得像一条死狗</p>\n\n<p>3、想方设法开除你还不想给补偿，直到逼你自己走为止。</p>\n\n<p>4、公司福利很差，社保是最低标准，公积金有跟没有没啥区别。没有旅游，没有节日礼物。聚餐还他么经费不够自己掏钱。</p>', '0', '0', '', '', '', '1499588398', '1', '0');
