INSERT INTO `co_company` VALUES ('10378', 'AwGV9vL', '杭州安存网络科技有限公司', '杭州市', '', '', '拖延离职/乱扣工资', '<p>杭州安存网络科技有限公司在2017年初发生变革，公司100+开发人员被强制离职最后只剩下不到十人，经过2017年重组公司开发人员达到20人左右，直致2018年1，2月份拖延工资发放，导致大部分员工只能找马爸爸借钱过春节。3月份发放了拖欠的工资。我于2018年3月初递交离职申请，在我交接工作的一个月中，找各种理由各种借口来扣除我绩效工资，我不在扣除工资单上签字，就不给我开离职证明，由于快到中旬不想让社保断，只有妥协。毕业近四年了，从没有见过这样的公司，真心希望在外拼搏的同道不要入这个坑</p>', '0', '0', '', '', '', '1498640008', '1', '0');
