INSERT INTO `co_company` VALUES ('12662', 'JZzW3yJ', '四川锐思通科技有限公司', '成都市', '', '', '拖欠工资，推延离职，拒绝交接，内部混乱', '<p>经常拖欠工资，两三个月发一次，合同签两千多，美其名曰避税，实际发工资税照扣，查了税务，发现根本没给你缴税，都被吞了。\n推延离职，不给交接工作，打了离职报告，在日期临近前以工作未交接为由，不让离职。\n公司内部管理混乱，十几个人的公司能演出宫斗剧，风气恶劣，完全无技术氛围，领导无能，做不出产品，一直靠上级公司接济，以及钻政策漏洞，做假报告骗政府的补贴存活。</p>', '0', '0', '', '', '', '1547625537', '1', '0');
