INSERT INTO `co_company` VALUES ('12664', 'Pa4vMJV', '辽宁倾城信科技网络有限公司', '沈阳市', '', '', '拖欠工资', '<p>该公司拖欠离职员工公司，为了不给离职员工发工资，给员工扣屎盆子，负责人自己让客户在项目中期加钱，导致项目黄了的事也能推给离职人员，找其要工资，恶语相向，一口一个赛比脸的骂人，目前已经找了劳动仲裁，劳动仲裁和劳动监察踢皮球，说是公司经营地址与营业执照的地址不一致，他们没法跨区管理，让我去法院，目前在法院立案一个月了。</p>', '0', '0', '', '', '', '1546480115', '1', '0');
