INSERT INTO `co_company` VALUES ('12729', 'GqWY9PD', '成都信合诚网络科技有限公司', '成都市', '', '', '拖欠薪资，谎言太多。', '<p>本人于2019年3月18号入职，试用期三个月，试用期满2019年6月18号，转正当日询问社保事宜，然后被以事情太多为由，暂无时间处理驳回，且不给办理任何转正手续。便于2019年6月19号提出辞职，其同意并对本人离职证明签字。按照合同规定，离职交接完成后即可发放未结算工资，其说下月（2019年7月15号）和正常员工一起发放。本人便于7月15号询问，说总公司要等离职后满一个月才能发放。目前2019年8月17号，前两天15号讨要薪资，又说财务没有统计到离职当月的工作打卡情况。 其各种弄虚作假，实在令人恶心。</p>', '0', '0', '', '', '', '1566583076', '0', '0');
